INSERT INTO PUBLIC.DOFUS(ID, NOM, DESCRIPTION, NIVEAU, EFFETS_MINI, EFFETS_MAXI) VALUES
(1, 'Dofawa', STRINGDECODE('Ce Dofus ressemble \u00e9trangement \u00e0 un oeuf de Dragodinde sauvage... Pour une fois, les apparences ne sont peut-\u00eatre pas trompeuses.'), 6, 4315, 4316),
(2, 'Dofus Cawotte', 'Le Dofus Cawotte augmente la sagesse de son porteur.', 6, 4317, 4318),
(3, 'Dofus Emeraude', STRINGDECODE('Plus vert qu''un Tofu malade, le Dofus Emeraude fut cr\u00e9\u00e9 par Aerafal, le Dragon de l''Air. Pour cr\u00e9er quelque chose d''aussi vert, il devait avoir le mal de l''air. Un comble, non ?'), 6, 4319, 4320),
(4, 'Dofus Kaliptus', STRINGDECODE('Personne ne conna\u00eet le dragon \u00e0 l''origine de ce Dofus. On raconte juste qu''il aurait ing\u00e9r\u00e9 une quantit\u00e9 effroyable de feuilles de Kaliptus, avant de pondre et de mourir d''une terrible crise d''a\u00e9rophagie.'), 6, 4321, 4322),
(5, 'Dofus Ocre', STRINGDECODE('Cr\u00e9\u00e9 par Terrakourial, le Dragon de la Terre, ce Dofus concentre de grands pouvoirs \u00e0 ne pas mettre entre toutes les mains... Ni entre tous les pieds d''ailleurs.'), 6, 4323, 4324),
(6, 'Dofus Pourpre', STRINGDECODE('C''est le Dragon du Feu Ignemikhal qui cr\u00e9a le Dofus Pourpre, un \u0153uf dont le moins qu''on puisse dire, c''est qu''il sent le soufre - mais il sent bien d''autres choses encore...'), 6, 4325, 4326),
(7, 'Dofus Turquoise', STRINGDECODE('Le Dofus Turquoise fut cr\u00e9\u00e9 par Aguabrial, le Dragon de l''Eau. Quand on y pense, c''est fou ce qu''un Dragon peut faire avec de l''eau...'), 6, 4327, 4328),
(8, 'Dofus Vulbis', STRINGDECODE('Gr\u00e2ce \u00e0 ce Dofus, vous jouirez d''une grande popularit\u00e9 aupr\u00e8s du sexe oppos\u00e9. Si seulement vous arr\u00eatiez de croire na\u00efvement tout ce que l''on vous raconte \u00e0 propos des Dofus et de leurs vertus.'), 6, 4329, 4330);  
