INSERT INTO PUBLIC.CLASSES(ID, NOM, DESCRIPTION, IMAGE, ARME1, ARME2, PALIERS_VITALITE, PALIERS_SAGESSE, PALIERS_FORCE, PALIERS_INTELLIGENCE, PALIERS_CHANCE, PALIERS_AGILITE, VIE, PA, PM, PO, INI, PP) VALUES
(9, 'Pandawa', STRINGDECODE('Les Pandawas sont des Guerriers porteurs qui savent faire des folies de leurs corps ! Ils peuvent m\u00eame en faire avec le corps des autres. En effet, le Pandawa sait soulever ses alli\u00e9s pour les prot\u00e9ger. Quant \u00e0 ses ennemis, il les enverra dans les airs pour les blesser !'), 'panda', 5, 8, 1, 2, 3, 4, 5, 6, 46, 6, 3, 0, 0, 100),
(11, 'Sadida', STRINGDECODE('Les Sadidas sont des Invocateurs qui fabriquent des onguents et des poisons redoutables ! Apprivoiser les Ronces pour en faire des armes terrifiantes, confectionner des poup\u00e9es de guerre et de soins, voil\u00e0 qui satisfait tout disciple Sadida digne de ce nom.'), 'sadi', 8, 7, 7, 8, 9, 10, 11, 12, 42, 6, 3, 0, 0, 100),
(12, 'Sram', STRINGDECODE('Les Srams sont des Assassins qui aiment les bourses, rebondies de pr\u00e9f\u00e9rence. Trousser les pans d''une tunique, t\u00e2ter le fond d''une poche, faire preuve de doigt\u00e9, palper enfin les bijoux tant convoit\u00e9s, poser enfin un pi\u00e8ge mortel, voil\u00e0 la vie d''un disciple de Sram !'), 'sram', 2, 1, 13, 14, 15, 16, 17, 18, 42, 6, 3, 0, 0, 100),
(6, STRINGDECODE('X\u00e9lor'), STRINGDECODE('Les X\u00e9lors sont des Mages qui ma\u00eetrisent le temps et toutes les m\u00e9caniques qui donnent l''heure : carillons, horloges, et pendules leur ob\u00e9issent au doigt et \u00e0 l''oeil. Les X\u00e9lors jouent donc avec le temps pour ralentir un ennemi ou se t\u00e9l\u00e9porter o\u00f9 bon leur semble.'), 'xelor', 11, 7, 19, 20, 21, 22, 23, 24, 42, 6, 3, 0, 0, 100),
(2, 'Ecaflip', STRINGDECODE('Les Ecaflips sont des Guerriers joueurs qui se fourrent dans les endroits o\u00f9 l''on peut gagner gros, et perdre beaucoup... Un Ecaflip bien dans sa peau joue sans arr\u00eat, pour tout et pour rien. Mais attention, il prend le jeu tr\u00e8s au s\u00e9rieux et ira m\u00eame jusqu''\u00e0 jouer sa vie sur un lancer de d\u00e9s pour gagner un combat...'), 'eca', 3, 2, 25, 26, 27, 28, 29, 30, 46, 6, 3, 0, 0, 100),
(3, 'Eniripsa', STRINGDECODE('Les Eniripsas sont des Soigneurs qui gu\u00e9rissent d''un simple Mot. Ils les utilisent surtout pour soigner leurs alli\u00e9s, mais parfois aussi pour blesser leurs ennemis. Parmi eux, certains sont m\u00eame devenus chasseurs de mots, arpenteurs du verbe, ou r\u00f4deurs des langues oubli\u00e9es.'), 'eni', 7, 8, 31, 32, 33, 34, 35, 36, 42, 6, 3, 0, 0, 100),
(4, 'Enutrof', STRINGDECODE('Les Enutrofs sont des Chasseurs de tr\u00e9sor inv\u00e9t\u00e9r\u00e9s ! Ils s''int\u00e9ressent \u00e0 toute denr\u00e9e, objet, ou cr\u00e9ature dont ils pourront tirer des kamas. La premi\u00e8re chose que fait un Enutrof lorsqu''il rencontre un autre aventurier, c''est le soupeser du regard. C''est vrai : aux yeux d''un Enutrof, un aventurier, c''est une cr\u00e9ature charg\u00e9e de denr\u00e9es et bard\u00e9e d''objets... revendables !'), 'enu', 9, 11, 37, 38, 39, 40, 41, 42, 42, 6, 3, 0, 0, 120),
(1, STRINGDECODE('Cr\u00e2'), STRINGDECODE('Les Cr\u00e2s sont des Archers aussi fiers que pr\u00e9cis ! Ils font des alli\u00e9s pr\u00e9cieux contre les adeptes de la m\u00eal\u00e9e franche. Restant \u00e0 distance, d\u00e9cochant leurs traits empenn\u00e9s dans le moindre orifice laiss\u00e9 sans surveillance, ils s''y entendent pour faire des d\u00e9g\u00e2ts !'), 'cra', 1, 2, 43, 44, 45, 46, 47, 48, 44, 6, 3, 0, 0, 100),
(5, STRINGDECODE('F\u00e9ca'), STRINGDECODE('Les F\u00e9cas sont de loyaux Protecteurs. Ils sont appr\u00e9ci\u00e9s dans les groupes d''aventuriers \u00e0 cause de leurs pouvoirs de protection, mais aussi parce qu''ils manient le b\u00e2ton comme pas deux. Des g\u00e9n\u00e9rations de F\u00e9cas s''enorgueillissent d''\u00eatre des d\u00e9fenseurs hors pair \u00e0 qui les Sages ont confi\u00e9 les objets les plus pr\u00e9cieux qui soient, en toute confiance.'), 'feca', 8, 7, 49, 50, 51, 52, 53, 54, 42, 6, 3, 0, 0, 100);          
INSERT INTO PUBLIC.CLASSES(ID, NOM, DESCRIPTION, IMAGE, ARME1, ARME2, PALIERS_VITALITE, PALIERS_SAGESSE, PALIERS_FORCE, PALIERS_INTELLIGENCE, PALIERS_CHANCE, PALIERS_AGILITE, VIE, PA, PM, PO, INI, PP) VALUES
(7, 'Iop', STRINGDECODE('Les Iops sont des Guerriers fonceurs et sans reproches ! Une chose est s\u00fbre : ils savent faire parler les armes. D''ailleurs, verser le premier sang au moins une fois par jour est pour eux un signe de bonne sant\u00e9. Leur temp\u00e9rament imp\u00e9tueux fait des Iops des paladins de l''extr\u00eame, capables du meilleur... comme du pire !'), 'iop', 3, 11, 55, 56, 57, 58, 59, 60, 48, 6, 3, 0, 0, 100),
(8, 'Osamodas', STRINGDECODE('Les Osamodas sont des Invocateurs n\u00e9s ! Ils ont le pouvoir d''invoquer des cr\u00e9atures et sont de remarquables dresseurs. Une rumeur pr\u00e9tend qu''ils taillent leurs v\u00eatements dans la peau de leurs ennemis, mais allez donc leur demander ce qu''il en est... Vous verrez bien si vous terminerez votre vie sous la forme d''une botte ou d''un bonnet fourr\u00e9.'), 'osa', 11, 8, 61, 62, 63, 64, 65, 66, 42, 6, 3, 0, 0, 100),
(10, 'Sacrieur', STRINGDECODE('Les Sacrieurs sont des Berzerkers qui d\u00e9cuplent leurs forces d\u00e8s qu''ils sont frapp\u00e9s ! N''ayant pas peur de recevoir des coups, ni de s''exposer aux blessures, ils seront souvent en premi\u00e8re ligne, et donc tr\u00e8s appr\u00e9ci\u00e9s par les groupes d''aventuriers ! Le Sacrieur est vraiment le compagnon id\u00e9al pour vos longues soir\u00e9es guerri\u00e8res...'), 'sacri', 0, 0, 67, 68, 69, 70, 71, 72, 46, 6, 3, 0, 0, 120);    
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(1, 3, STRINGDECODE('Ep\u00e9e Maudite du Saigneur Guerrier'), STRINGDECODE('Cette \u00e9p\u00e9e d\u00e9gage un pouvoir impressionnant, les carnages qu''elle augure sont de ceux restant dans les livres d''histoire. Mais son maniement reste \u00e9trangement hasardeux. Il existe peut-\u00eatre quelqu''un capable de corriger cela.'), 200, NULL, 0, 5, 1, 100, 10, 2, 1, 2, 3, 4, 5, 1, 2, ''),
(2, 3, 'Az''tech', STRINGDECODE('Cette \u00e9p\u00e9e appartenait \u00e0 un vieil Enutrof m\u00e9galomane, elle est en or massif, comme l''\u00e9tait son \u00e9go.'), 186, X'', 0, 5, 1, 45, 7, 50, 6, 7, 8, 0, 0, 3, 4, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 1 PA(s) \u00e0 la cible.\n')),
(3, 3, STRINGDECODE('Ep\u00e9e Lopon\u00e8ze'), STRINGDECODE('Cette \u00e9p\u00e9e construite \u00e0 partir de fragments de mycoses, parasitera \u00e0 merveille les intestins de vos ennemis.'), 184, X'', 0, 4, 1, 50, 10, 50, 9, 10, 0, 0, 0, 5, 6, ''),
(4, 3, 'Kukri Kura', STRINGDECODE('Souriez ! Dites cheese ! Voil\u00e0, vous \u00eates coup\u00e9 en deux.'), 179, X'', 0, 4, 1, 40, 7, 50, 11, 12, 0, 0, 0, 7, 8, '');        
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(5, 3, STRINGDECODE('L''\u00c9p\u00e9e Nice'), STRINGDECODE('Comme disait Brice : \u00ab Ce n''est pas sa taille qui compte, mais la fa\u00e7on de s''en servir. \u00bb'), 177, X'', 0, 3, 1, 50, 5, 50, 13, 0, 0, 0, 0, 9, 10, ''),
(6, 3, STRINGDECODE('L''Ep\u00e9e Rilleuse'), STRINGDECODE('Cette \u00e9p\u00e9e illustre bien un vieux proverbe Iop, quand l''ennemi p\u00e9rit, l''\u00e9p\u00e9e rit.'), 169, X'', 0, 5, 1, 50, 10, 50, 14, 15, 0, 0, 0, 11, 12, ''),
(7, 3, 'Fuschia', STRINGDECODE('Plus d''un fier guerrier a refus\u00e9 cette arme \u00e0 cause de sa couleur, la majorit\u00e9 d''entre eux est morte sous sa lame.'), 163, X'', 0, 5, 1, 30, 7, 50, 16, 17, 0, 0, 0, 13, 14, ''),
(8, 3, 'Karnak', STRINGDECODE('Cette \u00e9p\u00e9e l\u00e9gendaire a \u00e9t\u00e9 forg\u00e9e au petit matin, par une arm\u00e9e d''Enutrofs saouls, dans le fond des mines de Grimoth. Elle d\u00e9gage une aura de puissance surprenante, mais aussi une odeur de bi\u00e8re \u00e9vent\u00e9e.'), 158, X'', 0, 5, 1, 50, 10, 80, 18, 19, 0, 0, 0, 15, 16, '');  
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(9, 3, 'Griffe Pourpre', STRINGDECODE('Cette \u00e9p\u00e9e ac\u00e9r\u00e9e a pris cette teinte pourpre si \u00e9l\u00e9gante apr\u00e8s avoir \u00e9visc\u00e9r\u00e9 des milliers de Bworks. Alors qu''avant, elle n''\u00e9tait que violet fonc\u00e9.'), 145, X'', 0, 6, 1, 45, 8, 100, 20, 21, 0, 0, 0, 17, 18, ''),
(10, 3, 'Le Sabre Ayassalama', STRINGDECODE('Cette \u00e9p\u00e9e fut forg\u00e9e en visant uniquement l''esth\u00e9tisme afin de d\u00e9corer une chemin\u00e9e de grande valeur, mais son efficacit\u00e9 lors des tueries lui vaut d''\u00eatre mani\u00e9e r\u00e9guli\u00e8rement. Les gens n''ont d\u00e9cid\u00e9ment aucun respect pour l''art.'), 143, X'', 0, 4, 1, 80, 15, 50, 22, 23, 0, 0, 0, 19, 20, ''),
(42, 3, STRINGDECODE('Ep\u00e9e Royale du Bouftou'), STRINGDECODE('Cette \u00e9p\u00e9e ne sait pas trop sur quel sabot danser... Elle inflige des d\u00e9g\u00e2ts de chaque \u00e9l\u00e9ment.'), 70, NULL, 4, 4, 1, 30, 4, 50, 66, 67, 68, 69, 0, 83, 84, ''),
(11, 3, STRINGDECODE('Glaive Ic\u00e9ration'), STRINGDECODE('Ce glaive appartenait \u00e0 Metzger le boucher, qui s''en servait pour r\u00e9cup\u00e9rer rapidement les entrailles de ses ennemis pour en faire de la chair \u00e0 saucisse qu''il revendait \u00e0 bon prix sur les march\u00e9s de Bonta.'), 135, X'', 0, 4, 1, 50, 5, 50, 24, 25, 0, 0, 0, 21, 22, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 1 PA(s) \u00e0 la cible.\n'));     
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(12, 3, STRINGDECODE('Ep\u00e9e du Dragoeuf'), STRINGDECODE('Aiguis\u00e9e comme le rasoir du soir, cette lame vous permettra tout de m\u00eame de vous raser le matin, mais uniquement les aisselles.'), 127, X'', 0, 5, 1, 40, 8, 50, 26, 27, 0, 0, 0, 23, 24, ''),
(13, 3, STRINGDECODE('Ep\u00e9e du Dark Vlad'), STRINGDECODE('Seul le Dark Vlad en personne est \u00e0 m\u00eame de faire ressurgir toute la magie de cette \u00e9p\u00e9e, pour la rendre r\u00e9ellement \u00e9tincelante. En la portant, vous pourrez fi\u00e8rement montrer que vous n''avez pas fini comme ces ridicules aventuriers qui ont p\u00e9ri sous sa lame.'), 126, X'', 0, 5, 1, 50, 12, 100, 28, 29, 0, 0, 0, 25, 26, ''),
(14, 3, 'Griffe de Grizmine', STRINGDECODE('Grizmine n''a jamais r\u00e9ussi \u00e0 utiliser correctement cette griffe. A force de jouer aux cartes et aux jeux d''argent tout en buvant de la bi\u00e8re, ses muscles ont vite fini par s''atrophier. Chez certaines personnes, il semblerait que ce type de loisirs puisse m\u00eame atrophier d''autres organes.'), 111, X'', 0, 5, 1, 50, 5, 50, 30, 0, 0, 0, 0, 27, 28, '');
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(15, 3, STRINGDECODE('Ep\u00e9e du Rat Blanc'), STRINGDECODE('Cette Arme est le symbole du Rat Blanc. Elle est r\u00e9put\u00e9e pour \u00eatre capable de trancher ennemis et alli\u00e9s avec la m\u00eame facilit\u00e9.'), 109, X'', 67, 5, 1, 40, 5, 50, 31, 32, 0, 0, 0, 29, 30, ''),
(16, 3, 'Razoir du Soir', STRINGDECODE('Tous les vieux Enutrofs vous le diront, une belle barbe est une barbe entretenue. Sa lame est si finement aiguis\u00e9e, qu''elle conviendra m\u00eame aux dames un peu trop poilues.'), 106, X'', 0, 4, 1, 50, 10, 50, 33, 0, 0, 0, 0, 31, 32, ''),
(17, 3, STRINGDECODE('Ep\u00e9e Doncule'), STRINGDECODE('Cette \u00e9p\u00e9e brillante est id\u00e9ale pour cueillir des fleurs, m\u00eame si elles se d\u00e9fendent. Elle est m\u00eame parfaite pour cueillir des arbres.'), 100, X'', 0, 8, 1, 40, 15, 50, 34, 0, 0, 0, 0, 33, 34, '');           
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(18, 3, 'Griffe Rose', STRINGDECODE('Cette \u00e9p\u00e9e, d''une couleur peu commune, permet d''attendrir l''ennemi avant d''attendrir sa chair. Comme le ferait une douce Ecaflipette. Sa teinte particuli\u00e8re est obtenue en trempant la lame dans des visc\u00e8res de monstres pendant plusieurs centaines d''ann\u00e9es.'), 100, X'', 0, 5, 1, 50, 10, 40, 35, 0, 0, 0, 0, 35, 36, ''),
(46, 3, 'Le Cutter Sanglant', STRINGDECODE('Cette \u00e9p\u00e9e est totalement appropri\u00e9e pour agresser de gentilles personnes. Rapide, maniable et surtout extr\u00eamement coupante. Elle est, de ce fait, interdite \u00e0 la vente depuis longtemps. Mais tol\u00e9r\u00e9e aujourd''hui comme d\u00e9fense anti-bwork.'), 64, NULL, 0, 5, 1, 30, 10, 50, 74, 0, 0, 0, 0, 91, 92, ''),
(58, 3, 'Corbalame', 'Terriblement tranchante et incroyablement classe, cette lame risque de faire des jaloux parmi votre entourage de Corbacs.', 45, NULL, 8, 5, 1, 20, 5, 50, 86, 0, 0, 0, 0, 115, 116, ''),
(19, 3, 'Lame du Chef Bwork', 'Tranchante comme une lame de Bwork, cette lame du Chef Bwork a l''avantage de peser beaucoup plus lourd. Elle est certes moins maniable, mais il suffit de la laisser tomber sur son adversaire pour le trancher en deux, telle une vulgaire tranche de Tronknyde. Si votre ennemi ne meurt pas de douleur, il mourra de honte, et vous de rire.', 100, X'', 19, 5, 1, 40, 10, 50, 36, 0, 0, 0, 0, 37, 38, ''),
(20, 3, 'Patte d''Ecaflip', STRINGDECODE('Le Dieu Ecaflip vous a tendu sa patte, mais vous n''avez pu saisir qu''une griffe...  Et encore, elle ne coupait pas aussi bien que vous auriez pu le croire \u00e0 premi\u00e8re vue. En fait, ce n''\u00e9tait peut-\u00eatre pas le Dieu Ecaflip, finalement...'), 100, X'', 0, 5, 1, 50, 10, 40, 37, 0, 0, 0, 0, 39, 40, '');   
