(132, 5, 'Halle de Barde', STRINGDECODE('Cette hache tire son nom du test qui fut utilis\u00e9 pour prouver sa terrible efficacit\u00e9 : une hache g\u00e9ante, un mythophobiaque et une halle remplie de bardes.'), 99, X'', 0, 5, 1, 40, 15, 40, 181, 0, 0, 0, 0, 263, 264, ''),
(133, 5, 'Hache du Yondanwa', STRINGDECODE('Cette hache est utilis\u00e9e par les disciples Pandawushuka, lorsqu''ils obtiennent le grade de Yondanwa. Elle est \u00e9galement tr\u00e8s pratique pour ravager les for\u00eats de Bambous et tous ceux qui s''y cachent.'), 98, X'', 0, 5, 1, 50, 5, 50, 182, 183, 0, 0, 0, 265, 266, ''),
(134, 5, 'Hache de Lookabeer', STRINGDECODE('Cette hache est r\u00e9put\u00e9e pour son efficacit\u00e9 \u00e0 terminer d\u00e9finitivement et tr\u00e8s rapidement une querelle entre Enutrofs ivres.'), 96, X'', 0, 5, 1, 40, 10, 40, 184, 0, 0, 0, 0, 267, 268, '');  
