(321, 1, 'Arc Chtelion', STRINGDECODE('Cet arc d''une couleur discutable est parfait pour parader au Zaap de Bonta et \u00e0 d\u00e9faut de vous faire passer pour un coq, il attirera toutes les poulettes. Tant\u00f4t renversant, tant\u00f4t r\u00e9vulsant, il viendra peut-\u00eatre \u00e0 bout de vos ennemis sans que vous ne d\u00e9cochiez la moindre fl\u00e8che.'), 129, X'', 0, 4, 6, 35, 7, 40, 427, 428, 0, 0, 0, 641, 642, ''),
(322, 1, 'Arc Huledela', STRINGDECODE('Cet arc \u00e9tait avant tout utilis\u00e9 comme une arme de dissuasion. Son propri\u00e9taire expliquait \u00e0 qui voulait l''entendre que ses fl\u00e8ches \u00e9taient empoisonn\u00e9es, ce qui ne manquait pas d''effrayer la population locale. Ainsi, il arrivait \u00e0 faire reculer tous les pleutres qui se mettaient en travers de son chemin.'), 124, X'', 0, 5, 6, 30, 10, 50, 429, 430, 0, 0, 0, 643, 644, ''),
(323, 1, 'Jakchir Arc', STRINGDECODE('Cet arc est r\u00e9put\u00e9 pour voler l''argent du peuple \u00e0 distance, et en toute discr\u00e9tion. Le plus surprenant c''est qu''il reste tout de m\u00eame tr\u00e8s populaire au fil des ann\u00e9es. Heureusement pour le peuple, il commence d\u00e9j\u00e0 \u00e0 s''user.'), 124, X'', 0, 5, 6, 30, 10, 2, 431, 0, 0, 0, 0, 645, 646, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 2 &agrave; 3 PA(s) \u00e0 la cible.\nVole 1 \u00e0 5000 d''or.\n'));       
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(324, 1, 'Arc du Dragoeuf', STRINGDECODE('Cet arc est si puissant qu''il permet d''envoyer des oeufs pourris de Dragoeufs au dessus des murailles de Br\u00e2kmar. Quel beau g\u00e2chis !'), 122, X'', 0, 5, 8, 40, 10, 50, 432, 0, 0, 0, 0, 647, 648, ''),
(325, 1, 'Kask''Arc Go', STRINGDECODE('La corde de cet arc est faite de bave de Kaskargo, plus ou moins s\u00e8che, son \u00e9lasticit\u00e9 est sans pareille, sa texture gluante et visqueuse en revanche rappelle... On ne sait pas trop ce que \u00e7a rappelle mais \u00e7a n''est pas agr\u00e9able.'), 122, X'', 96, 5, 10, 30, 10, 50, 433, 434, 0, 0, 0, 649, 650, ''),
(326, 1, 'Arc Rainier', STRINGDECODE('Cet arc est en r\u00e9alit\u00e9 la chevelure fossilis\u00e9e d''un Sadida endormi trop profond\u00e9ment, sur laquelle des Araknes ont tiss\u00e9 leurs toiles.'), 121, X'', 0, 5, 6, 30, 5, 50, 435, 436, 0, 0, 0, 651, 652, '');               
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(327, 1, 'Arc Chitecte', STRINGDECODE('Cet arc \u00e0 l''apparence complexe est un v\u00e9ritable objet d''art. Le sculpteur d''arc qui l''a cr\u00e9\u00e9 est \u00e9galement c\u00e9l\u00e8bre pour avoir expos\u00e9 ses mod\u00e8les les plus controvers\u00e9s dans la fameuse grotte de Loopine, interdite au public non averti depuis des lustres.'), 120, X'', 0, 5, 8, 30, 10, 50, 437, 0, 0, 0, 0, 653, 654, ''),
(351, 1, 'Arc de Boo', STRINGDECODE('Cet arc prend sa force aussi bien de la terre que de l''eau. Malheur \u00e0 ceux qui ne ma\u00eetrisent ni l''un ni l''autre...'), 50, NULL, 0, 5, 8, 30, 10, 50, 473, 474, 0, 0, 0, 701, 702, ''),
(328, 1, 'Arc Hanne', STRINGDECODE('Cet arc a \u00e9t\u00e9 enchant\u00e9 il y a bien longtemps par une magie des plus obscures. De quoi inqui\u00e9ter autant son propri\u00e9taire que ses ennemis.'), 120, X'', 0, 5, 7, 30, 10, 50, 438, 439, 0, 0, 0, 655, 656, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 1 PA(s) \u00e0 la cible.\n')),
(329, 1, 'Arc du Chafer Archer', STRINGDECODE('Le Chafer Archer, n''aura jamais \u00e9t\u00e9 assez astucieux et habile \u00e0 cause de ses os tremblants, pour manier un tel arc. Except\u00e9 pour le probl\u00e8me des os tremblants, il est probable que vous soyez dans le m\u00eame cas que ce ridicule Chafer.'), 120, X'', 0, 5, 7, 30, 10, 50, 440, 441, 0, 0, 0, 657, 658, '');
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(330, 1, 'Arc Assin', STRINGDECODE('Cet arc \u00e9tait avant tout utilis\u00e9 pour chasser les Sangliers des Plaines. En effet, il est si puissant, qu''il permet \u00e0 l''aide d''une seule fl\u00e8che, de tuer plusieurs petits marcassins, \u00e0 condition qu''ils soient correctement align\u00e9s.'), 107, X'', 0, 5, 8, 30, 10, 50, 442, 443, 0, 0, 0, 659, 660, ''),
(331, 1, 'Arc Anum', STRINGDECODE('Cet arc est la preuve que l''association de la technologie et de la magie, peut donner d''excellents r\u00e9sultats. Pourtant, cet arc est tout de m\u00eame tomb\u00e9 rapidement dans l''oubli pour des raisons encore m\u00e9connues. L''artisan \u00e0 l''origine de cette arc a d''ailleurs \u00e9t\u00e9 contraint de fermer sa boutique, ce qui n''a pas manqu\u00e9 d''attrister bon nombre de ses anciens clients.'), 96, X'', 0, 5, 6, 30, 10, 50, 444, 445, 0, 0, 0, 661, 662, ''),
(332, 1, 'Arc du Sram Archer', STRINGDECODE('Quand les dagues ne suffisent plus, que les dieux se moquent de vous, \u00e0 vous de rechercher votre propre style de combat. Certains Srams l''ont bien compris, d''autres esp\u00e8rent que les dieux leur viennent en aide.'), 90, X'', 0, 5, 7, 30, 10, 50, 446, 447, 0, 0, 0, 663, 664, '');  
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(333, 1, 'Arc Chiduc', STRINGDECODE('Chiduc \u00e9tait un sculpteur d''arcs, r\u00e9put\u00e9 pour avoir des probl\u00e8mes de prononciation. Il \u00e9tait en effet incapable de prononc\u00e9 le son \"ar\" sans rajouter un \"keu\" derri\u00e8re. C''\u00e9tait tout de m\u00eame un grand arctiste.'), 87, X'', 0, 5, 6, 30, 10, 30, 448, 0, 0, 0, 0, 665, 666, ''),
(334, 1, 'Arc Ange', STRINGDECODE('A propos de cet arc, on est en droit de se demander pourquoi son nom est aussi stupide. Il en est de m\u00eame pour vous.'), 82, X'', 0, 5, 8, 30, 10, 50, 449, 0, 0, 0, 0, 667, 668, ''),
(335, 1, 'Geish''Arc', STRINGDECODE('Cet Arc est l\u00e9ger, fin, \u00e9l\u00e9gant, et sent bon la fleur de cerisier. Il est \u00e9galement meurtrier si l''on s''en sert pour autre chose que se pavaner.'), 81, X'', 0, 4, 7, 30, 5, 50, 450, 451, 0, 0, 0, 669, 670, ''),
(336, 1, 'Arc Boutant', STRINGDECODE('Cet arc a une forme tr\u00e8s \u00e9tudi\u00e9e, sa courbure est vraiment parfaite. On raconte \u00e0 ce sujet que le sculpteur d''arc s''est servi d''un fessier de femelle Bouftou comme patron.'), 80, X'', 0, 5, 8, 30, 10, 50, 452, 0, 0, 0, 0, 671, 672, '');             
