(353, 1, STRINGDECODE('L''Arc \u00e0 Hick'), STRINGDECODE('Hick \u00e9tait un Cr\u00e2 maudit : il n''arrivait jamais \u00e0 se d\u00e9barrasser de son hoquet. Cependant, il finit un jour par jeter son arc au feu pour le remplacer par une \u00e9p\u00e9e, et son mal disparu myst\u00e9rieusement...'), 50, NULL, 0, 5, 6, 40, 5, 40, 476, 0, 0, 0, 0, 705, 706, ''),
(354, 1, STRINGDECODE('Arc dit K\u00e9ha, nouvelle version'), STRINGDECODE('Type d''arc fabriqu\u00e9 seulement dans le grand nord d''Amakna, le K\u00e9ha est un arc peu cher, \u00e0 monter soi-m\u00eame. Ses performances d\u00e9pendent ainsi fortement de la ma\u00eetrise de l''assembleur.'), 47, X'', 0, 4, 7, 50, 15, 50, 477, 0, 0, 0, 0, 707, 708, ''),
(355, 1, 'Arc des Rivages', STRINGDECODE('Cet arc permettait aux p\u00eacheurs de nettoyer les rivages de toute la vermine environnante sans avoir \u00e0 se lever de leur si\u00e8ge, et cela depuis la fen\u00eatre du bar des pirates.'), 46, X'', 0, 4, 10, 30, 10, 45, 478, 0, 0, 0, 0, 709, 710, ''),
