(415, 1, 'Arc Hisedaisange', STRINGDECODE('Cet arc l\u00e9ger et fragile a s\u00e9duit bon nombre d''aventuriers, plus attach\u00e9s aux apparences qu''\u00e0 l''efficacit\u00e9.'), 143, X'', 0, 5, 6, 30, 5, 50, 552, 553, 554, 0, 0, 829, 830, ''),
(416, 1, 'Arc Lavoine', STRINGDECODE('Cet arc appartenait \u00e0 un m\u00e9nestrel ind\u00e9pendant, surtout c\u00e9l\u00e8bre, pour d\u00e9truire les oreilles de ses ennemis avec ses chants de guerre. Il fut exil\u00e9 pendant plusieurs ann\u00e9es sur Pandala, \u00e0 cause de ses chants qui \u00e9taient capables de rendre sourds les Corbacs de Br\u00e2kmar. Apr\u00e8s avoir rendu fous plusieurs g\u00e9n\u00e9rations de Pandawas, il fut tout simplement livr\u00e9 vivant au grand Sorlok, pour \u00eatre dig\u00e9r\u00e9 lentement pendant plus de 752 ann\u00e9es. Il y d\u00e9couvrit une nouvelle forme de plaisir intense.'), 130, X'', 0, 5, 6, 30, 10, 50, 555, 556, 0, 0, 0, 831, 832, '');
INSERT INTO PUBLIC.ARMES(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, PA, PO, CC, BONUS_CC, EC, DEGATS_1, DEGATS_2, DEGATS_3, DEGATS_4, DEGATS_5, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(417, 1, 'Arc Chtelion', STRINGDECODE('Cet arc d''une couleur discutable est parfait pour parader au Zaap de Bonta et \u00e0 d\u00e9faut de vous faire passer pour un coq, il attirera toutes les poulettes. Tant\u00f4t renversant, tant\u00f4t r\u00e9vulsant, il viendra peut-\u00eatre \u00e0 bout de vos ennemis sans que vous ne d\u00e9cochiez la moindre fl\u00e8che.'), 129, X'', 0, 4, 6, 35, 7, 40, 557, 558, 0, 0, 0, 833, 834, ''),
(418, 1, 'Arc Huledela', STRINGDECODE('Cet arc \u00e9tait avant tout utilis\u00e9 comme une arme de dissuasion. Son propri\u00e9taire expliquait \u00e0 qui voulait l''entendre que ses fl\u00e8ches \u00e9taient empoisonn\u00e9es, ce qui ne manquait pas d''effrayer la population locale. Ainsi, il arrivait \u00e0 faire reculer tous les pleutres qui se mettaient en travers de son chemin.'), 124, X'', 0, 5, 6, 30, 10, 50, 559, 560, 0, 0, 0, 835, 836, ''),
(419, 1, 'Jakchir Arc', STRINGDECODE('Cet arc est r\u00e9put\u00e9 pour voler l''argent du peuple \u00e0 distance, et en toute discr\u00e9tion. Le plus surprenant c''est qu''il reste tout de m\u00eame tr\u00e8s populaire au fil des ann\u00e9es. Heureusement pour le peuple, il commence d\u00e9j\u00e0 \u00e0 s''user.'), 124, X'', 0, 5, 6, 30, 10, 2, 561, 0, 0, 0, 0, 837, 838, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 2 &agrave; 3 PA(s) \u00e0 la cible.\nVole 1 \u00e0 5000 d''or.\n'));       
