(515, 7, 'Demi-Baguette', STRINGDECODE('Rika Zarouil la boulang\u00e8re fut connue pour avoir \u00e9t\u00e9 l''eniripsa la plus petite de la contr\u00e9e. Elle faisait son pain dans des petits-fours en carton et se battait avec des demi-baguettes.'), 11, NULL, 0, 4, 2, 30, 5, 50, 696, 0, 0, 0, 0, 1029, 1030, ''),
(423, 1, 'Arc Oleptik', STRINGDECODE('Cet arc \u00e9trange a \u00e9t\u00e9 sculpt\u00e9 par une Sadida pendant une phase de somnambulisme cr\u00e9atrice. Il en \u00e9mane une envie de sommeil qui affecte autant le porteur que ses cibles.'), 185, X'', 0, 4, 8, 30, 10, 50, 567, 568, 0, 0, 0, 845, 846, ''),
