(578, 8, STRINGDECODE('B\u00e2ton de Dina, dit ''le Mythe'''), STRINGDECODE('Ce b\u00e2ton a appartenu \u00e0 la tr\u00e8s belle Dina ''Mythe'', qui \u00e9tait surtout connue pour ses formes avantageuses et son caract\u00e8re explosif... Bref c''\u00e9tait une vraie bombe.'), 40, X'', 0, 5, 1, 30, 5, 50, 788, 0, 0, 0, 0, 1155, 1156, ''),
(579, 8, STRINGDECODE('B\u00e2ton du Puissant Tabi'), STRINGDECODE('Ce b\u00e2ton servait \u00e0 l''origine aux \u00e9leveurs d''Amakna qui voulaient contr\u00f4ler plus facilement leurs troupeaux de Tabis, ce b\u00e2ton est en effet si l\u00e9ger, qu''il permet \u00e0 son porteur de courir apr\u00e8s les Tabis \u00e9gar\u00e9s sans se fatiguer.'), 40, X'', 0, 3, 1, 40, 5, 50, 789, 0, 0, 0, 0, 1157, 1158, ''),
(590, 8, 'Fourbasse-Ton', STRINGDECODE('\"Pour la baston, rien ne vaut un Fourbasse-Ton\". Voici ce que l''on pouvait lire sur le d\u00e9pliant publicitaire de l''Agence Touristes, qui proposait de reconstruire soi-m\u00eame ce b\u00e2ton \u00e0 partir de diverses ressources cach\u00e9es un peu partout sur l''\u00eele de Moon. Une sorte de jeu de piste, mais avec aucune piste.'), 32, NULL, 16, 4, 1, 20, 15, 70, 800, 0, 0, 0, 0, 1179, 1180, STRINGDECODE('Fait perdre PA perdus &agrave; la cible : 1 PA(s) \u00e0 la cible.\n')),
