(629, 9, 'Pelle Moh', STRINGDECODE('Avec cette pelle, Le capitaine Machab a \u00e9ventr\u00e9 Grobill Dick, le grand Kralamoure Blanc.'), 115, X'', 0, 4, 1, 50, 10, 50, 850, 0, 0, 0, 0, 1257, 1258, ''),
(630, 9, STRINGDECODE('Pelle H\u00e9buse'), STRINGDECODE('Le forgeur de cette pelle a voulu rester anonyme pour faire le modeste, mais il a pris soin de laisser un indice dans le nom de cette pelle. Certains artisans sont vraiment irr\u00e9cup\u00e9rables...'), 94, X'', 0, 5, 1, 50, 5, 50, 851, 0, 0, 0, 0, 1259, 1260, '');    
