(784, 2, 'Sertisseur du Joaillomage', STRINGDECODE('Ce sertisseur permet de rendre plus \u00e9blouissants, les bijoux que vous avez confectionn\u00e9s.'), 1, NULL, 0, 6, 1, 40, 5, 40, 1041, 1042, 1043, 1044, 0, 1567, 1568, ''),
(719, 2, 'Dagues Hischantes', STRINGDECODE('Ces dagues feraient craquer n''importe quelle petite Sramette en qu\u00eate d''exp\u00e9riences nouvelles. En effet, ses pouvoirs \u00e9tranges lui permettent d''accomplir les pires fourberies, aux amis comme aux ennemis. Le Captain Chafer aurait ador\u00e9.'), 80, X'', 0, 3, 1, 30, 10, 50, 967, 968, 0, 0, 0, 1437, 1438, ''),
(720, 2, 'Lames Sanglantes D''Ortimus Contrari', STRINGDECODE('Ortimus Contrari est une l\u00e9gende parmi les assassins. Il ex\u00e9cute toujours ses contrats d''une mani\u00e8re totalement oppos\u00e9e aux m\u00e9thodes traditionnelles. N''en demandez pas plus, vous pourriez mourir dans l''instant.'), 80, X'', 0, 3, 1, 20, 15, 50, 969, 0, 0, 0, 0, 1439, 1440, ''),
(721, 2, 'Chakra Style', STRINGDECODE('Chakra Style est une paire de dagues ayant appartenu \u00e0 l\u2019une des plus grandes voleuses de tous les temps. Chakra fut la premi\u00e8re \u00e0 pouvoir forcer les portes du temple d''Osaroth et \u00e0 y d\u00e9rober de pr\u00e9cieuses reliques.'), 73, X'', 0, 3, 1, 30, 10, 50, 970, 0, 0, 0, 0, 1441, 1442, '');           
