INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(1, 1, 'Kralamansion', STRINGDECODE('Cette effrayante amulette est longtemps rest\u00e9e cach\u00e9e dans un manoir perdu. Autrefois vivante, elle voulait conqu\u00e9rir le monde. Finalement elle n''aura que votre cou. La pauvre.'), 194, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 97, 2099, 2100, ''),
(2, 1, 'Amunite', STRINGDECODE('Ce bijou d\u00e9gage une telle puissance qu''elle tuerait un Moskito \u00e0 30 pas, si son odeur n''emp\u00eachait pas ce dernier d''approcher \u00e0 moins d''un kilom\u00e8tre.'), 189, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 107, 2101, 2102, ''),
(3, 1, 'Amulette Ripage', STRINGDECODE('Un jour, le Captain Chafer rencontra le Bworker, il prit l''initiative de lui dire : \"ton post\u00e9rieur, ta t\u00eate, quelle diff\u00e9rence ?\". Depuis ce jour, personne n''a revu le Captain Chafer. M\u00eame son c\u00e9l\u00e8bre slip moulant en cuir semble avoir disparu.'), 184, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 108, 2103, 2104, ''),
(4, 1, 'Ougamulette', STRINGDECODE('Cette r\u00e9plique approximative du grand Ougah se porte autour du cou, avec un regard noir et une barbe correctement entretenue.'), 181, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 118, 2105, 2106, ''),
(5, 1, 'Zothulette', STRINGDECODE('Cette amulette est un vulgaire morceau de bois et de tissu d\u00e9chir\u00e9, mais les Zoths croient dur comme fer \u00e0 ses pouvoirs. Les pauvres fous !'), 162, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2107, 2108, ''),
(6, 1, 'Collier du Minotot', STRINGDECODE('Ce collier permettait aux Minotot, de prot\u00e9ger ses cervicales et d''\u00e9viter de p\u00e9rir \u00e0 cause d''un stupide coup du Wabbit.'), 158, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 57, 2109, 2110, ''),
(7, 1, STRINGDECODE('Collier du Tanukou\u00ef San'), STRINGDECODE('Ce collier est port\u00e9 par le Tanukou\u00ef San depuis sa tendre enfance, ce qui lui conf\u00e8re une valeur ajout\u00e9e non n\u00e9gligeable lorsque l''on sait de quoi il \u00e9tait capable, m\u00eame durant son enfance.'), 152, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2111, 2112, ''),
(8, 1, 'La Meulette', STRINGDECODE('La Meulette renferme une \u00e9trange substance trouv\u00e9e dans les entrailles du Meulou, d''aucuns pr\u00e9tendent qu''il s''agit de l''essence du monstre, d''autres pensent qu''il avait juste d\u00e9vor\u00e9 un Kirboule pas tr\u00e8s frais.'), 143, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 55, 2113, 2114, ''),
(9, 1, STRINGDECODE('Talisman du Ch\u00eane Mou'), STRINGDECODE('Cette pi\u00e8ce de bois pr\u00e9lev\u00e9e au coeur du Ch\u00eane mou renferme un grand pouvoir, comme celui de mettre le Ch\u00eane Mou en col\u00e8re.'), 138, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 66, 2115, 2116, ''),
(10, 1, 'Talisman d''Elya Wood', STRINGDECODE('Ce Talisman a \u00e9t\u00e9 confectionn\u00e9 avec de la poussi\u00e8re d''\u00e9toiles et de pierres pr\u00e9cieuses, prises dans de l''ambre d''Abraknyde Ancestral. Le m\u00e9lange \u00e9tant parfois explosif, il est d\u00e9conseill\u00e9 d''en fabriquer une vous-m\u00eame, laissez cela \u00e0 un Bijoutier que vous n''aimez pas.'), 135, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 39, 2117, 2118, '');               
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(11, 1, STRINGDECODE('Collier C\u00e9r\u00e9monial du Seigneur des Rats'), STRINGDECODE('Afin d''\u00e9viter qu''on le lui vole, ce collier est gliss\u00e9 autour du cou de l''h\u00e9ritier du Seigneur des rats \u00e0 sa naissance. Ainsi, \u00e0 l''\u00e2ge adulte, le seul moyen de le lui enlever est de lui couper la t\u00eate. On le lui vole tout de m\u00eame.'), 134, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 64, 2119, 2120, ''),
(12, 1, 'Plimclik', STRINGDECODE('Sous ce nom \u00e9trange se cache un objet tout aussi \u00e9trange, n\u00e9 on ne sait trop comment de pr\u00e9c\u00e9dentes amulettes. On ignore tout des circonstances dans lesquelles elle a \u00e9t\u00e9 d\u00e9couverte.'), 130, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2121, 2122, ''),
(13, 1, 'Amulette du Renouveau', STRINGDECODE('C''est le c\u00e9l\u00e8bre et non moins burlesque artisan Yamato, injustement m\u00e9pris\u00e9 pour son arc ridicule, qui le premier, eu l''audace de tenter d''enchanter une amulette avec autant d''effets, chose r\u00e9put\u00e9e infaisable depuis des lustres.'), 126, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2123, 2124, ''),
(14, 1, STRINGDECODE('M\u00e9daille Kido'), STRINGDECODE('Cette m\u00e9daille appartenait \u00e0 un c\u00e9l\u00e8bre Pandawa, ma\u00eetre d''une vieille \u00e9cole d''arts martiaux qui enseignait \u00e0 ses disciples la d\u00e9fense en utilisant la force de ses adversaires. Son \u00e9cole a fini par dispara\u00eetre car ses adversaires n''\u00e9taient plus assez forts.'), 125, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2125, 2126, ''),
(15, 1, 'Amulette du Dragoeuf', STRINGDECODE('Confectionn\u00e9e \u00e0 partir d''\u00e9cailles de Dragoeufs, cette amulette prot\u00e8gera efficacement votre pomme d''Adam, ou vos amygdales.  De quoi faire la diff\u00e9rence sur un champ de bataille.'), 124, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 56, 2127, 2128, ''),
(16, 1, 'La Feuille de Printemps', STRINGDECODE('Cette Amulette sp\u00e9cialement con\u00e7ue pour les vieux Enutrofs, a permis il y a bien longtemps \u00e0 Setag, de nettoyer la mine de Grimoth, de tous ses rivaux mineurs. Une m\u00e9thode certes peu raisonnable, mais terriblement efficace.'), 123, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2129, 2130, ''),
(17, 1, 'Collier du Minotoror', STRINGDECODE('Ce collier permettait au Minotoror de ne pas avoir de puces, de tiques, ou m\u00eame de poux. De toute \u00e9vidence, vous en aviez besoin.'), 111, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 54, 2131, 2132, ''),
(18, 1, 'Torque Ancestral', STRINGDECODE('Autour du cou, ce torque met en valeur votre barbe. Si vous n''avez pas de barbe, vous ne pouvez vous en prendre qu''\u00e0 vous-m\u00eame.'), 110, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 65, 2133, 2134, ''),
(19, 1, 'Ouassulette', 'Ce bijou essaye parfois de vous voler des aliments au passage pendant votre repas, ne le laissez pas pendre trop bas.', 105, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 77, 2135, 2136, ''),
(47, 1, 'Amulette du Koalak', STRINGDECODE('Cette amulette ne ressemble \u00e0 aucune autre amulette, tout simplement car aucune amulette ne lui ressemble.'), 60, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 46, 2191, 2192, '');              
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(20, 1, 'Collier du Rat Blanc', STRINGDECODE('Si vous le portez comme un collier, il vous prot\u00e8gera des \u00e9manations toxiques provenant des \u00e9go\u00fbts. En revanche, si vous le portez comme une ceinture, ce sont vos alli\u00e9s qui seront prot\u00e9g\u00e9s de vos \u00e9manations toxiques.'), 103, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 67, 2137, 2138, ''),
(21, 1, 'Collier du Rat Noir', STRINGDECODE('Le rat qui poss\u00e9dait ce collier pensait devenir le chef de son clan. Il s''est fait manger par un Lenald enrag\u00e9.'), 103, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 68, 2139, 2140, ''),
(22, 1, 'Amulette du Chef Bwork', STRINGDECODE('D''apparence rudimentaire, cette amulette en forme de silex taill\u00e9 est pourtant dot\u00e9e d''une magie tr\u00e8s puissante capable de transformer un Bwork en princesse, ou l''inverse, personne ne sait.'), 100, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 19, 2141, 2142, ''),
(23, 1, 'Collier du Dragon Cochon', STRINGDECODE('Ce collier dispose d''une petite boucle, qui permettait d''accrocher une laisse au cou du Dragon Cochon, lorsque son propri\u00e9taire voulait l''emmener faire une petite promenade nocturne.'), 100, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 58, 2143, 2144, ''),
(24, 1, 'Amulette de la Bouboule', STRINGDECODE('Un vieux dicton de Sufokia dit: de la Boule un jour, de la Bouboule peut-\u00eatre un autre jour. Vous pourrez constatez que, plus le dicton est vieux, plus il s''av\u00e8re ridicule.'), 98, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2145, 2146, ''),
(25, 1, 'La Feuille d''Hiver', STRINGDECODE('Cette magnifique broche est un hommage \u00e0 la c\u00e9l\u00e8bre r\u00e9sistance des mineurs de la mine d''argent de Grimoth, qui pass\u00e8rent un hiver entier, \u00e0 combattre un Rockailleux de la pire esp\u00e8ce, pour finalement mourir achev\u00e9s par un vieux Tofu \u00e9gar\u00e9.'), 98, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2147, 2148, ''),
(26, 1, STRINGDECODE('La R\u00e9sinette'), STRINGDECODE('Cette amulette en r\u00e9sine de Bambou diffusera autour de vous une odeur agr\u00e9able. Tr\u00e8s efficace pour dissimuler vos essais de Vents de Panique.'), 94, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2149, 2150, ''),
(27, 1, 'Amulette Feudala', STRINGDECODE('Seuls les habitants du village de Feudala avaient l''autorisation de porter cette amulette, qui est cens\u00e9e \u00eatre un des \u00e9l\u00e9ments les plus importants de leur costume traditionnel. En y regardant de plus pr\u00e8s, on se demande bien pourquoi.'), 91, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 37, 2151, 2152, ''),
(28, 1, 'Amublop Multicolore Royale', 'Sans additif ni colorant, cette amulette serait tout simplement transparente. Rassurez-vous, personne ne vous demandera de la manger.', 90, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 106, 2153, 2154, ''),
(29, 1, 'La Feuille d''Automne', STRINGDECODE('Cette Broche a \u00e9t\u00e9 confectionn\u00e9e un matin d''automne au milieu d''une for\u00eat de H\u00eatres et d''Ormes non loin de l''\u00eele d''Olag-lacustre. Elle est consid\u00e9r\u00e9e par nombre de b\u00fbcherons comme une v\u00e9ritable relique.'), 89, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2155, 2156, '');
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(30, 1, 'Amufafah', STRINGDECODE('Ce collier donne une furieuse envie de rugir \u00e0 son possesseur, simplement parce que les moustaches du Mufafah qui l''ornent lui chatouillent les t\u00e9tons.'), 84, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2157, 2158, ''),
(31, 1, STRINGDECODE('Amulette du Craqueleur L\u00e9gendaire'), STRINGDECODE('D''apparence modeste, cette amulette renferme bien des secrets notamment celui pr\u00e9cisant quels secrets elle renferme.'), 84, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 52, 2159, 2160, ''),
(32, 1, 'Amulette Argie', STRINGDECODE('Cette \u00e9trange amulette, permet \u00e0 son porteur de s''endormir pour une bonne sieste en plein combat. Un peu comme un F\u00e9ca le ferait contre un groupe de Craqueleurs.'), 82, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2161, 2162, ''),
(33, 1, 'Amublop Coco Royale', STRINGDECODE('Cette amulette co\u00fbte beaucoup plus cher \u00e0 fabriquer qu''une Amublop classique. Mais il faut voir le bon c\u00f4t\u00e9 des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d''Astrub.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 104, 2163, 2164, ''),
(34, 1, 'Amublop Griotte Royale', STRINGDECODE('Cette amulette co\u00fbte beaucoup plus cher \u00e0 fabriquer qu''une Amublop classique. Mais il faut voir le bon c\u00f4t\u00e9 des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d''Astrub.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 102, 2165, 2166, ''),
(35, 1, 'Amublop Indigo Royale', STRINGDECODE('Cette amulette co\u00fbte beaucoup plus cher \u00e0 fabriquer qu''une Amublop classique. Mais il faut voir le bon c\u00f4t\u00e9 des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d''Astrub.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 105, 2167, 2168, ''),
(36, 1, 'Amublop Reinette Royale', STRINGDECODE('Cette amulette co\u00fbte beaucoup plus cher \u00e0 fabriquer qu''une Amublop classique. Mais il faut voir le bon c\u00f4t\u00e9 des choses, elle peut aussi se revendre plus cher, surtout si vous arnaquez un habitant d''Astrub.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 103, 2169, 2170, ''),
(37, 1, 'Amulette Terrdala', STRINGDECODE('Seuls les habitants du village de Terrdala avaient l''autorisation de porter cette amulette, qui est cens\u00e9e \u00eatre un des \u00e9l\u00e9ments les plus importants de leur costume traditionnel. En y regardant de plus pr\u00e8s, on se demande bien pourquoi.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 36, 2171, 2172, ''),
(38, 1, 'Epis de Farle', STRINGDECODE('Cette amulette d''apparence rudimentaire a \u00e9t\u00e9 confectionn\u00e9e \u00e0 partir de trois \u00e9pis de Bl\u00e9 magiques, recouverts de poussi\u00e8re de pierre pr\u00e9cieuse, et fauch\u00e9s dans les plaines de Snossios pendant la treizi\u00e8me moisson d''Istrabraq. Elle conf\u00e8re un courage hors du commun \u00e0 son porteur, d\u00e9sormais \u00e0 m\u00eame de faucher plaines et vall\u00e9es sans jamais faillir.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 17, 2173, 2174, ''),
(40, 1, 'La Doubitch O''Boul', STRINGDECODE('Cette amulette est en fait une confiserie. Elle pourrait \u00eatre mang\u00e9e si elle n''avait pas auparavant \u00e9t\u00e9 roul\u00e9e sous les aisselles pendant 36 g\u00e9n\u00e9rations, par une horde de femelles Bwork en chaleur.'), 77, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 38, 2177, 2178, '');
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(39, 1, 'Epis de Shika', STRINGDECODE('Shika assistait Farle lors des moissons, en r\u00e9cup\u00e9rant les \u00e9pis magiques qui tombaient sous sa faux. Elle prit l''habitude au fil des moissons, de les accrocher autour de son cou. Priv\u00e9s de leurs racines et du reste de leur tige, ces \u00e9pis magiques continuent tout de m\u00eame de m\u00fbrir chaque ann\u00e9e \u00e0 la m\u00eame p\u00e9riode.'), 80, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 53, 2175, 2176, ''),
(41, 1, 'Amulette du Chef Crocodaille', STRINGDECODE('Cette surprenante amulette a en fait \u00e9t\u00e9 confectionn\u00e9e \u00e0 partir d''un Bouclier Reptilien dont on a d\u00e9tach\u00e9 les multiples pierres pr\u00e9cieuses. C''est donc un bijou particuli\u00e8rement lourd \u00e0 porter mais vraiment tr\u00e8s r\u00e9sistant. Votre cou sera parfaitement prot\u00e9g\u00e9 des vampires, mais aussi des su\u00e7ons des villageoises un peu trop entreprenantes.'), 70, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 20, 2179, 2180, ''),
(42, 1, STRINGDECODE('La Ma\u00eetrise des Poup\u00e9es'), STRINGDECODE('Epuop Tik en personne a command\u00e9 cette amulette \u00e0 un illustre bijoutier. Il d\u00e9sirait pouvoir habiller toutes ses poup\u00e9es en m\u00eame temps au lieu d''attendre qu''elles p\u00e9rissent pour en invoquer d''autres. Une maladie courante chez les Sadidas.'), 69, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2181, 2182, ''),
(43, 1, 'Amulette Turquoise', STRINGDECODE('De la couleur du c\u00e9l\u00e8bre Dofus Turquoise, cette amulette n''a en fait aucun rapport avec ce dernier, ce qui ne l''emp\u00eache pas d''\u00eatre tr\u00e8s puissante pour autant. Comme quoi, les apparences ne sont pas toujours trompeuses.'), 63, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2183, 2184, ''),
(44, 1, 'Amulette Aerdala', STRINGDECODE('Seuls les habitants du village d''Aerdala avaient l''autorisation de porter cette amulette, qui est cens\u00e9e \u00eatre un des \u00e9l\u00e9ments les plus importants de leur costume traditionnel. En y regardant de plus pr\u00e8s, on se demande bien pourquoi.'), 62, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 35, 2185, 2186, ''),
(45, 1, STRINGDECODE('Le Flan des \u00c9toiles'), STRINGDECODE('Cet objet a \u00e9t\u00e9 moul\u00e9 \u00e0 partir d''un m\u00e9lange d''oeufs c\u00e9lestes et de poussi\u00e8res d''\u00e9toiles lact\u00e9es. A conserver au frais.'), 62, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2187, 2188, ''),
(46, 1, 'Amulette "Dents de Wabbits"', STRINGDECODE('Les dents de cette amulette ont rong\u00e9 plus de Cawottes que Brouce Boulgoure n''a d\u00e9racin\u00e9 d''arbres, ne vous \u00e9tonnez donc pas si vous sentez la Cawotte, et si les Tiwabbits cherchent \u00e0 vous faire des bisous dans le cou.'), 60, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 11, 2189, 2190, ''),
(48, 1, 'Gelamu', STRINGDECODE('Voil\u00e0 une amulette, qui trahira votre go\u00fbt prononc\u00e9 pour les orgies de Gel\u00e9e. On peut trouver mieux pour se mettre en valeur ou briller en soci\u00e9t\u00e9, mais il faut assumer jusqu''au bout votre faible pour la gel\u00e9e.'), 60, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 6, 2193, 2194, ''),
(49, 1, 'Amuloumulette', STRINGDECODE('Derri\u00e8re ce surnom stupide, se cache en r\u00e9alit\u00e9 une amulette relativement puissante, confectionn\u00e9e \u00e0 partir de crocs, de dents et d''ossements de Mulous, ce qui explique la terrible odeur qui s''en d\u00e9gage. A d\u00e9faut d''\u00eatre assortie \u00e0 votre cou, elle sera assortie \u00e0 votre haleine.'), 57, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 32, 2195, 2196, ''); 
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(50, 1, 'Talisman du Faucheur d''Avoine', STRINGDECODE('\"Je ne suis pas un Tikoko, les Trukikols me collent \u00e0 la peau, je ne suis pas un Tikoko, un Tikokooooooo...\". Voil\u00e0 ce que chantait \u00e0 tue-t\u00eate en revenant de ses vacances sur l''\u00eele de Moon, Danioule le c\u00e9l\u00e8bre m\u00e9nestrel paysan, connu pour battre l''Avoine en chantant.'), 55, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2197, 2198, ''),
(51, 1, STRINGDECODE('Amulette de X\u00e9lor'), STRINGDECODE('Cette amulette surpuissante est le fruit du travail sur la ma\u00eetrise du temps d''un tr\u00e8s vieux X\u00e9lor.'), 52, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2199, 2200, ''),
(52, 1, 'Amulette du Craqueleur des Plaines', STRINGDECODE('Cette amulette renferme une partie de l''histoire des Craqueleurs des Plaines qui pourrait se r\u00e9sumer en deux lignes si quelqu''un la connaissait. Malheureusement, les Craqueleurs eux-m\u00eames ne la connaissent pas.'), 52, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2201, 2202, ''),
(53, 1, 'L''Oeil de Kanigrou', STRINGDECODE('Offert \u00e0 Melyonen par le c\u00e9l\u00e8bre Kanigrou  Rook Y Balboua, pour la remercier de lui avoir pr\u00e9sent\u00e9 celle qui est maintenant sa femme, Ar d\u2019rieeeeeeeeeneuh comme il aime \u00e0 l\u2019appeler.'), 52, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2203, 2204, ''),
(54, 1, STRINGDECODE('M\u00e9daille Oly'), STRINGDECODE('Oly \u00e9tait une disciple de Iop dont le seul talent \u00e9tait la cuisine. On rapporte que Iop en personne lui aurait donn\u00e9 cette m\u00e9daille, pour compenser ses trop grandes faiblesses en combat. Cependant seuls les coeurs purs peuvent la porter.'), 51, X'', 0, 2205, 2206, ''),
(55, 1, 'Amublop Coco', STRINGDECODE('Quand Lorkos voulut p\u00e9n\u00e9trer dans le Labyrinthe du Minotoror, il n''h\u00e9sita pas une seconde \u00e0 \u00e9quiper son Amublop. Personne n''a jamais su pourquoi.'), 50, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 100, 2207, 2208, ''),
(56, 1, 'Amublop Griotte', STRINGDECODE('Quand Lorkos voulut p\u00e9n\u00e9trer dans le Labyrinthe du Minotoror, il n''h\u00e9sita pas une seconde \u00e0 \u00e9quiper son Amublop. Personne n''a jamais su pourquoi.'), 50, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 98, 2209, 2210, ''),
(57, 1, 'Amublop Indigo', STRINGDECODE('Quand Lorkos voulut p\u00e9n\u00e9trer dans le Labyrinthe du Minotoror, il n''h\u00e9sita pas une seconde \u00e0 \u00e9quiper son Amublop. Personne n''a jamais su pourquoi.'), 50, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 101, 2211, 2212, '');         
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(58, 1, 'Amublop Reinette', STRINGDECODE('Quand Lorkos voulut p\u00e9n\u00e9trer dans le Labyrinthe du Minotoror, il n''h\u00e9sita pas une seconde \u00e0 \u00e9quiper son Amublop. Personne n''a jamais su pourquoi.'), 50, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 99, 2213, 2214, ''),
(59, 1, 'Amulette Royale du Bouftou', 'Cette amulette augmente la force et l''intelligence de son porteur.', 50, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 4, 2215, 2216, ''),
(60, 1, 'Amulette de la Boule', STRINGDECODE('Cette Amulette est presque parfaite, elle accro\u00eet consid\u00e9rablement la sagesse de celui qui la porte autour du cou. Tr\u00e8s utile pour para\u00eetre plus sage aupr\u00e8s de vos amis, ou vous faire passer pour un \u00e9colier mod\u00e8le.'), 49, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2217, 2218, ''),
(61, 1, 'Amulette Papayou', STRINGDECODE('Cette amulette n''est en r\u00e9alit\u00e9 qu''un assortiment de fleurs accroch\u00e9es autour d''un fil. Elle est cens\u00e9e diffuser un parfum magique \u00e0 m\u00eame de transformer un Papayou en Papayou L\u00e9l\u00e9.'), 47, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 129, 2219, 2220, ''),
(62, 1, STRINGDECODE('Amulette des Ab\u00eemes'), STRINGDECODE('Cette amulette a \u00e9t\u00e9 retrouv\u00e9e dans l''\u00e9pave \u00e9chou\u00e9e du c\u00e9l\u00e8bre p\u00eacheur de Kralamoure, Moby Diqueriveurse, qui \u00e9tait \u00e9galement populaire pour ses boutades vaseuses au bar des pirates.'), 47, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2221, 2222, ''),
(63, 1, 'Amulette du Craqueleur', STRINGDECODE('Cette puissante amulette contient toute la vie du Craqueleur, tous les objets qu''il a r\u00e9colt\u00e9s avant de mourir sous les coups de butoir d''aventuriers attir\u00e9s par ses richesses.'), 47, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2223, 2224, ''),
(64, 1, 'Harmonie', STRINGDECODE('Ce pendentif aide son porteur \u00e0 se retrouver en paix avec le monde qui l''entoure.'), 47, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2225, 2226, ''),
(65, 1, 'Craquamulette', STRINGDECODE('Cette amulette est aussi lourde qu''un Blop boulimique, voil\u00e0 qui devrait d\u00e9courager les aventuriers un peu trop fragiles de la porter. En revanche, une fois son poids support\u00e9, il faut reconna\u00eetre qu''elle vous donne fi\u00e8re allure.'), 46, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 26, 2227, 2228, ''),
(66, 1, 'L''Abramu', STRINGDECODE('L''Abramu est une amulette tr\u00e8s \u00e9l\u00e9gante. Si vous avez l''intention de la porter nous vous conseillons de vous promener nu afin de bien mettre en valeur ce pr\u00e9cieux bijoux. Vous verrez, les dames n''y r\u00e9sistent pas.'), 46, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2229, 2230, ''),
(67, 1, STRINGDECODE('Amulette Scarabosse Dor\u00e9'), STRINGDECODE('Port\u00e9e sur un torse velu, cette amulette a tout pour plaire tant elle se marie bien avec les touffes de poils.'), 45, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 51, 2231, 2232, ''),
(68, 1, 'Amulette du Vampyre', STRINGDECODE('Cette amulette est cens\u00e9e pr\u00e9venir les Vampyres qui voudraient vous mordre dans le cou, qu''il est tout simplement inutile d''essayer de vous sucer le sang, puisque vous \u00eates vous-m\u00eame un Vampyre.'), 45, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 130, 2233, 2234, '');           
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(69, 1, STRINGDECODE('Cha\u00eene de la Liche'), STRINGDECODE('La Liche \u00e9tait une sale cr\u00e9ature connue pour s''incruster dans les groupes de valeureux guerriers afin de subtiliser une part du butin. Un jour, son petit man\u00e8ge fut d\u00e9couvert et elle dut prendre la fuite.'), 45, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2235, 2236, ''),
(70, 1, STRINGDECODE('La Feuille d''Et\u00e9'), STRINGDECODE('Cette petite broche a vraiment du charme, destin\u00e9e \u00e0 la base pour les malchanceux fermement d\u00e9cid\u00e9s \u00e0 participer aux jeux de la f\u00eate foraine, on a vite remarqu\u00e9, qu''elle \u00e9tait \u00e9galement bien adapt\u00e9e pour les chasses \u00e9piques.'), 45, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2237, 2238, ''),
(71, 1, STRINGDECODE('Chance \u00e0 Mulette'), STRINGDECODE('Mulette \u00e9tait une petite Ecaflipette qui n''a jamais eu de chance dans sa vie, jusqu''au jour o\u00f9 sa grand-m\u00e8re lui offrit cette amulette.'), 44, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2239, 2240, ''),
(72, 1, 'L''Amulette du Puzzle de la Griffe', STRINGDECODE('Autrefois, un Ecaflip nomm\u00e9 Dodge, \u00e9clata en mille morceaux une carte au tr\u00e9sor grav\u00e9e sur une pierre. Croyant r\u00e9ussir \u00e0 reformer la carte, il garda la plus grosse partie pour en faire une Amulette myst\u00e9rieuse. Nul ne sait ce que le tr\u00e9sor est devenu.'), 44, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2241, 2242, ''),
(73, 1, 'Amulette du Kam Assutra', STRINGDECODE('Cette amulette donne des facult\u00e9s tr\u00e8s particuli\u00e8res \u00e0 son porteur, en effet ce dernier se voit comme transport\u00e9 par une aura myst\u00e9rieuse parcourant son corps, ce qui g\u00e9n\u00e9ralement a pour effet de d\u00e9cupler ses capacit\u00e9s physiques.'), 43, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2243, 2244, ''),
(74, 1, 'La Chaplivate', STRINGDECODE('A la fois amulette et cravate, cet objet d''apparat permettra \u00e0 vos proches de vous tenir en laisse facilement. Et vous aimerez \u00e7a.'), 43, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 116, 2245, 2246, ''),
(75, 1, 'Tartamulette', STRINGDECODE('Si vous avez toujours r\u00eav\u00e9 de distribuer des tartes \u00e0 vos amis, offrez-leur cette amulette.'), 43, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 110, 2247, 2248, ''),
(76, 1, 'Amukwak de Flammes', STRINGDECODE('Avec toutes ces plumes autour du cou, vous ressemblerez \u00e0 Aldo Rado, grand Sacrieur aventurier en qu\u00eate d''une cit\u00e9 d''or.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 2, 2249, 2250, ''),
(77, 1, 'Amukwak de Glace', STRINGDECODE('Avec cette amulette autour du cou, vous pourriez tenter de faire croire \u00e0 votre entourage que vous avez une haleine fra\u00eeche. Laissez tomber, c''est peine perdue.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 13, 2251, 2252, ''),
(78, 1, 'Amukwak de Terre', STRINGDECODE('Jadis port\u00e9e par Oto Mustam, chef des guerriers de Br\u00e2kmar, cette petite amulette ne lui servait qu''\u00e0 se faire beau lors des orgies organis\u00e9es \u00e0 Br\u00e2kmar. Tout un programme.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 14, 2253, 2254, '');          
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(79, 1, 'Amukwak du Vent', STRINGDECODE('Lorsque le vent souffle dans ses plumes, le Kwak du Vent se sent libre comme l''air, et se laisse planer tel une vieille feuille morte emport\u00e9e par une bourrasque. Contrairement aux id\u00e9es re\u00e7ues, c''est assez impressionnant.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 15, 2255, 2256, ''),
(80, 1, STRINGDECODE('Dragolyre Remani\u00e9e'), STRINGDECODE('Gr\u00e2ce aux multiples talents des artisans d''Amakna, la Dragolyre Remani\u00e9e excelle dans la perfection.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2257, 2258, ''),
(84, 1, STRINGDECODE('Embl\u00e8me Livide'), STRINGDECODE('Cette amulette est extr\u00eamement utile, mais rend son porteur blanc comme un bouftou.'), 41, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2265, 2266, ''),
(81, 1, 'Le foulard du Lard Fou', STRINGDECODE('Le Lard Fou \u00e9tait un petit d\u00e9mon bien vil qui ne pouvait s''emp\u00eacher de rester plant\u00e9 devant les gens sans bouger. Il \u00e9tait aussi r\u00e9sistant \u00e0 la magie, ce qui imposait le respect \u00e0 l''\u00e9poque. Depuis, le Lard Fou se terre quelque part et attend.'), 42, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2259, 2260, ''),
(82, 1, 'Amulette Akwadala', STRINGDECODE('Seuls les habitants du village d''Akwadala avaient l''autorisation de porter cette amulette, qui est cens\u00e9e \u00eatre un des \u00e9l\u00e9ments les plus importants de leur costume traditionnel. En y regardant de plus pr\u00e8s, on se demande bien pourquoi.'), 41, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 34, 2261, 2262, ''),
(83, 1, 'Amulette du Pirate', STRINGDECODE('Cette amulette terrifiante a impressionn\u00e9 plus d''un marin, la porter sur soi suffit \u00e0 faire de vous une personne redout\u00e9e.'), 41, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2263, 2264, ''),
(85, 1, 'Ambramulette Moskito', STRINGDECODE('Porter autour du cou un Moskito pris dans un ambre, c''est un peu montrer \u00e0 vos adversaires, que vous \u00eates un redoutable chasseur... de Moskitos. De quoi vous rendre ridicule en dehors du Berceau.'), 40, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2267, 2268, ''),
(86, 1, 'Bambamulette', STRINGDECODE('Ce ridicule petit morceau de bambou est dot\u00e9 de pouvoirs magiques bien \u00e9tranges. Il \u00e9tait autrefois port\u00e9 par les jeunes Pandawas, une fois la pubert\u00e9 pass\u00e9e. Vous devriez peut-\u00eatre attendre encore quelques ann\u00e9es avant de le porter autour du coup...'), 40, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2269, 2270, ''),
(87, 1, STRINGDECODE('La Broche C\u00e9leste'), STRINGDECODE('On dit que cette broche a \u00e9t\u00e9 cr\u00e9\u00e9e par l''illustre forgeron Boulgur, qui a la r\u00e9putation de forger les armes des dieux.'), 40, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2271, 2272, ''),
(88, 1, 'Vegamu', STRINGDECODE('La Vegamu a parfois tendance \u00e0 attirer les Moskitos, qui de loin la prennent pour une copine. Ils sont souvent d\u00e9\u00e7us une fois qu''ils tombent sur le porteur de l''amulette.'), 40, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 10, 2273, 2274, ''),
(89, 1, 'Amulette du Mineur Sombre', STRINGDECODE('La seule chance de survivre dans une mine infest\u00e9e de Mineurs Sombres, c''est d''y voir clair. A d\u00e9faut de leur voler leur bougie, portez cette amulette, elle vous portera chance.'), 39, X'', 0, 2275, 2276, '');       
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(90, 1, 'Accord', STRINGDECODE('Ce pendentif aide son porteur \u00e0 se retrouver en paix avec le monde qui l''entoure.'), 38, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2277, 2278, ''),
(91, 1, 'Amulette de Silicate', STRINGDECODE('Silicate a confectionn\u00e9 cette Amulette un matin d''hiver, pour un c\u00e9l\u00e8bre aventurier du nom de Rimb Vardi, qui pourchassait un vieux Bwork Mage, responsable de plusieurs catastrophes naturelles d\u00e9clench\u00e9es par sa magie trop approximative.'), 37, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2279, 2280, ''),
(92, 1, STRINGDECODE('Amulette du P\u00eacheur de Somoon'), STRINGDECODE('P\u00eacher un Somoon rel\u00e8ve de l''exploit, mais avec cette amulette sur soi, plus rien n''est impossible.'), 35, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2281, 2282, ''),
(93, 1, 'Ambramulette Arackne', STRINGDECODE('Porter autour du coup une Arackne prise dans l''ambre, lorsque vous attaquez une de ses soeurs, peut suffire \u00e0 la d\u00e9contenancer. Pauvre b\u00eate, on appelle cela l''ironie du sort.'), 34, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2283, 2284, ''),
(94, 1, 'Amulette Deulegarnoulle', STRINGDECODE('Cette amulette provient des r\u00e9gions nordiques, elle conf\u00e8re a son porteur la capacit\u00e9 d''invoquer plus facilement des cr\u00e9atures.'), 33, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2285, 2286, ''),
(95, 1, 'Fourbamulette', STRINGDECODE('Cette amulette est constitu\u00e9e d''un m\u00e9lange de feuilles de Fourbasse et de d\u00e9tritus de l''\u00eele de Moon. Elle ressemble plus \u00e0 un collier de fleurs qu''\u00e0 une v\u00e9ritable amulette, mais elle vous permettra de s\u00e9duire sans trop d''efforts la faune locale de l''\u00eele de Moon, compos\u00e9e presque exclusivement d''abominables cr\u00e9atures. Ainsi, plus aucune Fourbasse ne saura vous r\u00e9sister, elles vous sauteront toutes au cou pour vous faire des su\u00e7ons. Les plus malchanceux en amour sauront s''en contenter.'), 33, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 16, 2287, 2288, ''),
(96, 1, 'Amulette du Badoul', STRINGDECODE('Dodge adorait exploser tout ce qui avait de la valeur \u00e0 ses yeux. Il disait que c''\u00e9tait la preuve qu''il existait. Chacun son truc.'), 32, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2289, 2290, ''),
(97, 1, 'Dragolyre', STRINGDECODE('Cette splendide amulette confectionn\u00e9e \u00e0 partir de restes de Dragoeuf, est suffisamment impressionnante pour faire battre en retrait la majorit\u00e9 des ennemis normalement constitu\u00e9s.'), 32, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2291, 2292, ''),
(98, 1, 'La Bourgeonette', STRINGDECODE('Gorg\u00e9e de soleil, la Bourgeonette r\u00e9chauffe les coeurs en plus de procurer une sensation d''invuln\u00e9rabilit\u00e9 face \u00e0 la terre. Elle est aussi tellement l\u00e9g\u00e8re qu''on oublie souvent de l''enlever \u00e0 l''heure du coucher.'), 32, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2293, 2294, ''),
(99, 1, STRINGDECODE('M\u00e9daille Holy'), STRINGDECODE('Holy \u00e9tait une disciple de Iop qui avait jur\u00e9 de terrasser le mal o\u00f9 qu''il soit. D''un calme redoutable, elle ne s''\u00e9nervait jamais sauf quand on la comparait \u00e0 une certaine Oly.'), 31, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2295, 2296, '');   
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(100, 1, STRINGDECODE('M\u00e9daille Lahilama'), STRINGDECODE('Lahilama \u00e9tait un grand chef spirituel qui officiait au fin fond des montagnes. Il s''ennuyait tellement, qu''il passait ses journ\u00e9es \u00e0 coudre des bonnets jaunes pour ses disciples. La folie n''a pas de limites.'), 31, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2297, 2298, ''),
(101, 1, 'La Bibelaw', STRINGDECODE('Law-bibe \u00e9tait un vieillard qui adorait assembler n''importe quoi pour en faire un objet. Un jour, il cr\u00e9a ce m\u00e9daillon aux formes intrigantes. Une fois autour du cou, il ressentit une \u00e9trange sensation. Bien \u00e9trange sensation m\u00eame...'), 30, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2299, 2300, ''),
(102, 1, 'Amulette de Shika', STRINGDECODE('Cette amulette a permis \u00e0 de nombreuses reprises \u00e0 Shika Ingalsse, de survivre aux rebellions des troupeaux de Bouftous bien d\u00e9cid\u00e9s \u00e0 lui mener la vie dure.'), 29, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2301, 2302, ''),
(103, 1, 'Kabale', STRINGDECODE('Symbole myst\u00e9rieux d''un organisme myst\u00e9rieux. D''ailleurs il n''existe pas, et vous n''en avez jamais entendu parler !'), 29, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2303, 2304, ''),
(104, 1, STRINGDECODE('La Broche H\u00e8te'), STRINGDECODE('H\u00e8te \u00e9tait l''un des plus grands guerriers d''Amakna. La l\u00e9gende dit qu''il tirait sa sagesse de la broche qu''il portait toujours sur lui.'), 28, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2305, 2306, ''),
(105, 1, STRINGDECODE('La C\u00e9l\u00e9brit\u00e9'), STRINGDECODE('Cette puissante amulette a tout pour rendre son porteur c\u00e9l\u00e8bre.'), 27, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2307, 2308, ''),
(106, 1, 'Amulette du Kitsou', 'Cette amulette du Kitsou attirera l''attention sur votre double menton.', 26, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 41, 2309, 2310, ''),
(107, 1, 'Entente', STRINGDECODE('Ce pendentif aide son porteur \u00e0 se retrouver en paix avec le monde qui l''entoure.'), 26, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2311, 2312, ''),
(108, 1, 'Larvamulette', STRINGDECODE('Avec cette t\u00eate de larve autour du cou, vous ressemblez \u00e0 Ulrik Holgan c\u00e9l\u00e8bre dompteur de larves des temps modernes.'), 26, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 33, 2313, 2314, ''),
(109, 1, 'Amulette en Crabe', STRINGDECODE('Cette amulette blanch\u00e2tre est confectionn\u00e9e avec du Crabe, ce qui en fait une amulette tr\u00e8s fragile mais originale.'), 24, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2315, 2316, ''),
(110, 1, STRINGDECODE('Amulette du B\u00fbcheron'), STRINGDECODE('Cette amulette, exclusivement fabriqu\u00e9e \u00e0 partir de bois pr\u00e9cieux, est tr\u00e8s pris\u00e9e des B\u00fbcherons qui lui attribuent bien souvent des pouvoirs qu''elle n''a pas.'), 22, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2317, 2318, ''),
(111, 1, 'Amulette du Marin', STRINGDECODE('Cette amulette, sertie d''\u00e9cailles de poisson, est cens\u00e9e porter chance \u00e0 son propri\u00e9taire lorsqu''il part en mer les jours de temp\u00eate.'), 22, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2319, 2320, '');          
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(112, 1, 'Amulette du Tofu', STRINGDECODE('L\u00e9g\u00e8re comme une plume de Tofu, cette amulette se porte autour du cou ou du front, pour les aventuriers en qu\u00eate d''originalit\u00e9.'), 22, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 50, 2321, 2322, ''),
(113, 1, 'La Reinette', STRINGDECODE('Cette amulette, est une am\u00e9lioration de La Destin\u00e9e Dor\u00e9e. \u00c9trangement, elles n''ont rien en commun, except\u00e9 leur aspect assez ridicule, il faut le reconna\u00eetre.'), 22, X'', 0, 2323, 2324, ''),
(114, 1, 'Amulette Paysanne', STRINGDECODE('Cette amulette est parfum\u00e9e \u00e0 l''huile de Tournesol Sauvage et aux fines herbes. Elle peut donc servir d''app\u00e2t pour les ruminants dangereux que vous voudrez pi\u00e9ger, comme les Bouftous, ou les belles-m\u00e8res par exemple.'), 20, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 47, 2325, 2326, ''),
(115, 1, 'Amulette de l''Homme Ours', STRINGDECODE('L''Homme Ours portait cette amulette autour du cou, lorsqu''il a affront\u00e9 les brigandins de la tani\u00e8re de Bimdhoul. La l\u00e9gende raconte qu''elle lui a sauv\u00e9 la vie, mais quand on la regarde de pr\u00e8s, on s''aper\u00e7oit qu''il s''agit bien d''une l\u00e9gende.'), 20, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 12, 2327, 2328, ''),
(116, 1, 'Amulette du Boufcoul', STRINGDECODE('Cette amulette \u00e9tait jadis port\u00e9e par tous les aventuriers qui pr\u00e9f\u00e9raient r\u00e9pandre l''amour plut\u00f4t que de faire la guerre. Chacun ses go\u00fbts.'), 20, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 43, 2329, 2330, ''),
(117, 1, 'L''Araknamu', STRINGDECODE('Cette petite Arakne est la preuve vivante que l''on est pas oblig\u00e9 d''empailler un animal pour en faire une Amulette. Elle vous chatouillera le cou, et tissera peut-\u00eatre une toile devant votre bouche, si vous baillez trop souvent, ou si vous avez trop mauvaise haleine.'), 20, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 3, 2331, 2332, ''),
(118, 1, 'Amulette du Chafer', STRINGDECODE('Le Chafer est quelqu''un de sensible et distingu\u00e9, il garde toujours avec lui une amulette faite des cr\u00e2nes de ses cousins tomb\u00e9s au combat. Le plus \u00e9trange, c''est que le Chafer n''a jamais eu de cousins.'), 19, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2333, 2334, ''),
(119, 1, STRINGDECODE('Amulette Itby remani\u00e9e'), STRINGDECODE('Excellent travail de l''apprenti-orf\u00e8vre Polma Kartnez, cette copie surpasse l''originale.'), 17, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2335, 2336, '');     
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(120, 1, 'La Gougnole', STRINGDECODE('La Gougnole a \u00e9t\u00e9 cr\u00e9\u00e9e dans de la pierre de lune ayant tremp\u00e9 5000 ans dans un baril de bi\u00e8re. In\u00e9vitablement, cela lui a conf\u00e9r\u00e9 de terribles pouvoirs.'), 17, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2337, 2338, ''),
(121, 1, 'Omelette Amulette', STRINGDECODE('L''osamodas Onehaieuf Bek cherchait absolument \u00e0 gagner du m\u00e9rite devant les membres de sa guilde, et d\u00e9cida alors de faire une Amulette r\u00e9volutionnaire. Il y parvint en pr\u00e9parant un petit d\u00e9jeuner \u00e0 ses amis. \u00c9trange.'), 17, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2339, 2340, ''),
(122, 1, 'Amulette du Bandit', STRINGDECODE('Cette amulette a jadis appartenu au grand bandit \"Thisme\", r\u00e9put\u00e9 pour sa cruaut\u00e9 sans limite et sa mort idiote. Sa vie f\u00fbt digne d''un conte d''Eniripsa, il mour\u00fbt noy\u00e9 et \u00e9cras\u00e9 dans un pressoir \u00e0 huile alors qu''il venait tout juste de purger une peine de prison de plus d''une centaine d''ann\u00e9es, pour avoir vol\u00e9 une noix de cajou.'), 16, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 25, 2341, 2342, ''),
(123, 1, 'Amulette du Moskito', STRINGDECODE('Moins efficace et moins pris\u00e9e que la c\u00e9l\u00e8bre Araknamu, cette petite amulette a pourtant beaucoup de charme lorsqu''elle est port\u00e9e toute seule, sur un corps tr\u00e8s poilu.'), 15, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 23, 2343, 2344, ''),
(124, 1, 'La Lou Ptiote', STRINGDECODE('Sylveline n''\u00e9tait pas tr\u00e8s fut\u00e9e. Sa m\u00e8re, Lou, lui offrit cette lanterne en disant \"Tiens! Au moins tu seras une lumi\u00e8re !\". Elle non plus n''\u00e9tait pas une lanterne. Ce qui ne l''a pas emp\u00each\u00e9e d''avoir un \u00e9clair de g\u00e9nie qui illumina la vie de sa fille.'), 15, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2345, 2346, ''),
(125, 1, 'La Veaux Doux', STRINGDECODE('Joz\u00e9bo Vez \u00e9tait un berger de Veaux, lointains cousins des Bouftous. Dans son troupeau, quelques b\u00eates poss\u00e9daient une fourrure plus douce que les autres. Elle se revendait bien plus cher et pour diff\u00e9rencier ses b\u00eates, il attachait ce collier \u00e0 leur cou.'), 15, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2347, 2348, ''),
(126, 1, STRINGDECODE('La Cha\u00eene Nhanor Kibrill'), STRINGDECODE('Son cr\u00e9ateur, un certain Ken Aton, \u00e9tait tr\u00e8s c\u00e9l\u00e8bre pour ses habitudes assez \u00e9tranges. Il passait ses journ\u00e9es \u00e0 chasser le Minotoror pour se fabriquer une moquette pectorale.'), 13, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2349, 2350, ''),
(127, 1, STRINGDECODE('La Destin\u00e9e Dor\u00e9e'), STRINGDECODE('La Destin\u00e9e Dor\u00e9e permet au h\u00e9ros qui la porte d''augmenter consid\u00e9rablement sa vitalit\u00e9 et son agilit\u00e9.'), 12, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2351, 2352, ''),
(128, 1, 'Amulette en Mousse', STRINGDECODE('Cette amulette est souvent confondue avec un jouet \u00e0 trouver dans des pochettes surprises, alors qu''en fait elle se gagne au stand de tir.'), 11, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 63, 2353, 2354, ''),
(129, 1, 'Amulette Itby', STRINGDECODE('Cr\u00e9ation originale de l''orf\u00e8vre John Les Nonnes, c''est aujourd''hui un des bijoux les plus classiques.'), 10, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 2355, 2356, '');             
