INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(1099, 8, 'Pioute violet', STRINGDECODE('Ce petit Pioute n''est pas comme les autres Pious, il suffit de regarder sa couleur pour s''en apercevoir. En revanche, il partage avec les autres Pious, un go\u00fbt prononc\u00e9 pour picorer les post\u00e9rieurs. Il para\u00eet que certains aiment... Ce familier ne mange que des graines.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4295, 4296, ''),
(1100, 8, 'Ross', STRINGDECODE('Ce magnifique Lapino est le familier pr\u00e9f\u00e9r\u00e9 du c\u00e9l\u00e8bre Ma\u00eetre Noir Jack. Ne vous fiez pas \u00e0 son apparence, il peut se montrer redoutable en combat. Ce familier ne mange que des Cawottes et des plantes.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4297, 4298, ''),
(1101, 8, 'Shushu de Rushu', STRINGDECODE('Ce d\u00e9mon mineur vous accompagnera durant toutes vos p\u00e9r\u00e9grinations, du Zaap d''Astrub au cimeti\u00e8re, voire m\u00eame du cimeti\u00e8re au Zaap d''Astrub. Cette cr\u00e9ature est anim\u00e9e d''une haine visc\u00e9rale envers les Ouassingues. Une sombre histoire de m\u00e9nage para\u00eet-il... Attaquez des Ouassingues, et elle se repa\u00eetra de leurs d\u00e9pouilles.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4299, 4300, ''),
(1102, 8, 'Tifoux', STRINGDECODE('Cette splendide cr\u00e9ature est un lointain cousin des c\u00e9l\u00e8bres Lenalds de l''\u00eele des Wabbits. Plus t\u00e9m\u00e9raire, mais \u00e9galement beaucoup plus indisciplin\u00e9e, elle saura vous apporter son aide durant vos combats, mais \u00e9galement faire preuve d''une fourberie sans nom, en vous aidant \u00e0 vaincre les membres de sa propre famille.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4301, 4302, ''),
(1103, 8, 'Tiwabbit Kiafin', STRINGDECODE('Cette cr\u00e9ature est un peu stupide, la seule fa\u00e7on de la faire avancer est de lui accrocher dans le dos un b\u00e2ton soutenant une Cawotte. Heureusement, ce familier a \u00e9t\u00e9 correctement dress\u00e9, et ne tentera pas d''envoyer des vents de panique. Ce d\u00e9voreur d''\u00e2mes se nourrit en mangeant les restes d''autres Wabbits.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4303, 4304, ''),
(1104, 8, 'Vampyrette', STRINGDECODE('Cette terrible cr\u00e9ature prendra un malin plaisir \u00e0 sucer le sang de vos amis, ou celui de vos chevilles si elles sont un peu trop grosses. Mais cela ne suffira pas \u00e0 la divertir pour autant, elle aime par dessus tout que vous affrontiez avec elle des squelettes pour se repa\u00eetre de leurs d\u00e9pouilles. On vous avait bien dit qu''elle \u00e9tait terrible.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4305, 4306, ''),
(1105, 8, 'Vilain Petit Corbac', STRINGDECODE('Ce sombre volatile est avant tout connu pour appr\u00e9cier les cimeti\u00e8res et les aventuriers \u00e0 la peau blanche. Il adore casser avec son gros bec, les ossements qu''il trouve dans les tombes.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 8, 4307, 4308, ''),
(1106, 8, 'Wabbit', STRINGDECODE('Ce petit Wabbit est domestiqu\u00e9. Il ne vous mordra donc pas. En revanche pour les autres...'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 11, 4309, 4310, ''),
(1108, 8, 'Willy le Relou', STRINGDECODE('Willy le Relou est vraiment une m\u00e9chante cr\u00e9ature. Son enfance a semble-t-il \u00e9t\u00e9 particuli\u00e8rement difficile. Il vous accompagnera partout et fera fuir vos amis.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4313, 4314, '');               
INSERT INTO PUBLIC.EQUIPEMENTS(ID, TYPE, NOM, DESCRIPTION, NIVEAU, CONDITIONS, PANOPLIE, EFFETS_MINI, EFFETS_MAXI, EFFETS_AUTRE) VALUES
(1107, 8, 'Walk', STRINGDECODE('De toute \u00e9vidence, cette cr\u00e9ature s''est \u00e9chapp\u00e9e d''un zoo ou d''une \u00eele lointaine. Elle est connue pour am\u00e9liorer les dommages de son ma\u00eetre lors des combats. Sa plus grande particularit\u00e9, (qu''elle partage parfois avec son ma\u00eetre) est d''avoir une haleine de poisson moisi. Nous vous recommandons de lui clouer le bec, m\u00eame si le marteau et les clous sont \u00e0 commander s\u00e9par\u00e9ment. Attention, cette petite cr\u00e9ature est tr\u00e8s exigeante, elle ne mange que du poisson.'), 1, X'aced0005737200136a6176612e7574696c2e41727261794c6973747881d21d99c7619d03000149000473697a6578700000000077040000000a78', 0, 4311, 4312, '');       
